number_of_variables = local_variables.length\ncounter = 0\nwhile counter < number_of_variables\n\t	if eval(local_variables[counter]).instance_of? Fixnum\n\t\tprint "int:"+local_variables[counter]+":"+eval(local_variables[counter]).to_s+":\n"\n\telsif eval(local_variables[counter]).instance_of? String\n\t\tprint "str:"+local_variables[counter]+":"+eval(local_variables[counter]).to_s+":\n"\n\telsif eval(local_variables[counter]).instance_of? Float\n\t\tprint "float:"+local_variables[counter]+":"+eval(local_variables[counter]).to_s+":\n"\n\tend\n\tcounter = counter + 1\nend
